import React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import useScrollTrigger from "@material-ui/core/useScrollTrigger";
import { makeStyles } from "@material-ui/styles";

//logo of nav bar
import logo from "../../assets/logo.svg";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";

function ElevationScroll(props) {
	const { children, window } = props;
	// Note that you normally won't need to set the window ref as useScrollTrigger
	// will default to window.
	// This is only being set here because the demo is in an iframe.
	const trigger = useScrollTrigger({
		disableHysteresis: true,
		threshold: 0,
		target: window ? window() : undefined,
	});

	return React.cloneElement(children, {
		elevation: trigger ? 4 : 0,
	});
}

const useStyles = makeStyles((theme) => ({
	toolbarMargin: {
		...theme.mixins.toolbar, //this helps to push the underneath content from any component i.e if some text is hide under any component it helps to uncover it
		marginBottom: "3em",
	},
	logo: {
		height: "7em",
	},
	tabContainer: {
		marginLeft: "auto",
	},
	tab: {
		fontFamily: "Raleway",
		textTransform: "none", //to lowercase the letter
		fontWeight: 700,
		fontSize: "1rem",
		minWidth: 10,
    mar
	},
}));

const Header = (props) => {
	const classes = useStyles();

	return (
		<React.Fragment>
			<ElevationScroll>
				<AppBar position="fixed">
					<Toolbar disableGutters>
						<img alt="company logo" className={classes.logo} src={logo} />
						<Tabs className={classes.tabContainer}>
							<Tab className={classes.tab} label="Home" />
							<Tab className={classes.tab} label="Service" />
							<Tab className={classes.tab} label="Revolution" />
							<Tab className={classes.tab} label="About Us" />
							<Tab className={classes.tab} label="Contact Us" />
						</Tabs>
					</Toolbar>
				</AppBar>
			</ElevationScroll>
			<div className={classes.toolbarMargin} />
		</React.Fragment>
	);
};

export default Header;
